mapper.select:
select distinct a.i_product, a.i_user,i_product_type,
title, p_nm, p_price, type_sub_title, hit, state,p_img_main,i_loc,
DATE_FORMAT(r_dt,'%Y-%m-%d') as show_time,(select count(i_product) from t_cmt where i_product = a.i_product) as
"cmt_count",(select count(i_user) from t_favorite where i_product = a.i_product group by i_product) is_favorite
from t_product_sale a
where i_product_type=#{i_product_type}


order by i_product desc;


order by p_price desc;


order by p_price asc;


order by cmt_count desc;


order by is_favorite desc;


order by i_product desc;

select distinct a.i_product, a.i_user,
title, p_nm, p_price, type_sub_title, hit, state,p_img_main,i_loc,
DATE_FORMAT(r_dt,'%Y-%m-%d') as show_time,(select count(i_product) from t_cmt where i_product = a.i_product) as
"cmt_count",(select count(i_user) from t_favorite where i_product = a.i_product group by i_product) is_favorite
from t_product_sale a
where type_sub_title=#{type_sub_title}


order by i_product desc;


order by p_price desc;


order by p_price asc;


order by cmt_count desc;


order by is_favorite desc;


order by i_product desc;

select * from
t_product_sale
where i_product = #{i_product} and i_user = #{i_user};

SELECT COUNT(i_user) as user_count
FROM t_favorite
WHERE i_product = #{i_product};

mapper.update:
update t_product_sale
set p_img_main = #{p_img_main}
where i_product = #{i_product}

UPDATE t_product_sale 
SET state = #{state}
WHERE i_product = #{i_product}

mapper.delete:
delete from t_product_sale
where i_user = #{i_user}
and i_product = #{i_product}
