mapper.select:
select *
from t_user


		i_user = ${i_user}
		AND isAuth = 1	


		id_email = #{id_email}
		AND isAuth = 1

mapper.update:
UPDATE t_user
SET isAuth = 1
WHERE i_user = #{i_user};

UPDATE t_user
SET profile_img = #{profile_img}
where i_user = #{i_user};

UPDATE t_user
SET user_pw = #{user_pw}
SET postcode = #{postcode}, roadAddr =#{roadAddr}, jibunAddr = #{jibunAddr}, extraAddr = #{extraAddr}, detailAddr = #{detailAddr}
SET ph= #{ph}
WHERE i_user = #{i_user}

UPDATE t_user
SET code = #{code}
where i_user = #{i_user}

UPDATE t_user
SET profile_img = NULL
WHERE i_user = #{i_user}
