mapper.select:
select profile_img from t_user


		where nick = #{recv_nick}


		where nick = #{send_nick}

select m.no,m.room,m.send_nick,m.recv_nick,date_format(m.send_time '%Y-%m-%d $H:$i') as send_time, m.read_time, m.content,m.read_chk,u.profil_img from t_ms m left outer join t_user u on m.send_nick = u.nick
where room=#{room}
where (recv_nick = ${recv_nick} and send_nick = #{nick}) or (send_nick = #{recv_nick} and recv_nick = #{nick})

mapper.update:
update t_ms set read_chk=1


		where room=#{room} and read_chk=0m and recv_nick=#{nick}


		where send_nick=#{recv_nick} and read_chk=0 and recv_nick=#{nick}
